export default class extends React.Component {
  render() {
    return (
      <div>

        <img src="platzi.png" alt="Platzi" />
        <h1>Creado por Stiven</h1>
        <p>¡Este curso está que arde!</p>
        <style jsx>
          {`
          div{
            padding: 3rem;
          }
          img{
            max-width: 50%;
            display: block;
            margin: 0 auto;
          }
          h1 {
            text-align: center;
            color: white;
          }
          p {
            text-align: center;
            color: white;
          }
          :global(body) {
              background-color: blue;
          }
        `}

        </style>
      </div>


    )
  }
}
